import { Component, OnInit } from '@angular/core';
import { OfferItemComponent } from '../offer-item/offer-item.component';
import { OfferModel } from 'src/app/core/marketplace-api/models/offer.model';

@Component({
  selector: 'app-offer-list',
  templateUrl: './offer-list.component.html',
  styleUrls: ['./offer-list.component.scss'],
  standalone: true,
  imports: [OfferItemComponent]
})
export class OfferListComponent implements OnInit {
  offers: OfferModel[] = [
    {
      title: "IPhone",
      category: "Usados",
      description: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Morbi a augue tristique, aliquam purus in, varius diam. Sed sagittis interdum orci, sed fringilla diam mattis sed.",
      location: "Bogotá D.C",
      pictureUrl: "iphone.jpg",
      publishedOn: new Date(),
      user: "David"
    }
  ];

  pageSize = 4;

  constructor() { }

  ngOnInit(): void {
  }

}
