import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, ReactiveFormsModule, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/core/services/auth.service';

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.scss'],
  standalone: true,
  imports: [ReactiveFormsModule],
})
export class SignUpComponent implements OnInit {
  signUpForm = new FormGroup({
    username: new FormControl('', [Validators.required])
  });

  constructor(private auth: AuthService, private router: Router) { }

  ngOnInit(): void {
  }

  async signUp(){
    this.signUpForm.markAsTouched()
    if(this.signUpForm.valid){
      try{
        await this.auth.signUp(this.signUpForm.controls.username.value).toPromise()
        this.router.navigate(["/login"]);
      } catch(e){
        console.log(e);
      }
    } else {
      alert("You must enter a valid username")
    }
  }

}
<form [formGroup]="signUpForm" (submit)="signUp()">
  <h3>Sign Up</h3>
  <div class="form-group">
    <label for="username">User</label>
    <input id="username" class="" placeholder="User Name"/>
  </div>
  <button type="submit" class="btn primary-btn offer-submit-button">Register</button>
</form>
