import {Component, Input, OnInit} from '@angular/core';
import {OfferModel} from '../../core/marketplace-api/models/offer.model';
import {  DatePipe } from '@angular/common';

@Component({
  selector: 'app-offer-item',
  templateUrl: './offer-item.component.html',
  styleUrls: ['./offer-item.component.scss'],
  standalone: true,
  imports: [DatePipe],
})
export class OfferItemComponent implements OnInit {

  @Input()
  offer: OfferModel;

  constructor() { }

  ngOnInit(): void {
  }

}
<div class="card">
  <div class="title-wrapper">
    <h3 class="title">{{offer.title}}</h3>
  </div>
  <div class="picture">
    <img src="/assets/{{offer.pictureUrl}}" alt="offer-image" />
  </div>
  <div class="body">
    <div class="byline">
      <span class="user">{{offer.user}}</span>
      <span class="published-on">{{offer.publishedOn | date }}</span>
    </div>
    <p class="description">{{offer.description}}</p>
    <div class="category">{{offer.category}}</div>
  </div>
</div>
