import { Component, Input, OnInit } from '@angular/core';
import { PageModel } from '../../models/page.model';
import { OfferModel } from '../../marketplace-api/models/offer.model';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-paginator',
  templateUrl: './paginator.component.html',
  styleUrls: ['./paginator.component.scss'],
  standalone: true,
  imports: [
    CommonModule
  ]
})
export class PaginatorComponent implements OnInit {

  pagesArr: Array<number>; //Array used to generate page buttons

  offset: number = 0;

  _pageConfig: PageModel<OfferModel>;

  @Input() set pageConfig(pageModel: PageModel<OfferModel>) {
    this._pageConfig = pageModel;
    this.pagesArr = [...Array(pageModel.pageCount).keys()];
  }


  constructor() { }

  ngOnInit(): void {
  }

  switchPage(pageIndex: number, index?){
    if(!(pageIndex < 0) && !(pageIndex > this._pageConfig.pageCount)) {
      this._pageConfig.pageIndex = pageIndex;
      // window.scroll(0,0);
      if(index >= 4){
        this.offset = (pageIndex+5) >= 10 ? (pageIndex-5) : this.offset;
      }else {
        // this.offset = 0;
      }
    } 
  }
}
