import {Component, Input, OnInit} from '@angular/core';
import {  DatePipe } from '@angular/common';
import { Offer } from 'src/app/core/models/offer.model';

@Component({
  selector: 'app-offer-item',
  templateUrl: './offer-item.component.html',
  styleUrls: ['./offer-item.component.scss'],
  standalone: true,
  imports: [DatePipe],
})
export class OfferItemComponent implements OnInit {

  @Input()
  offer: Offer;

  @Input()
  cardSize: "md" | "xl" = "md"; 

  constructor() { }

  ngOnInit(): void {
  }

}
<div class="card card-{{cardSize}}">
  <div class="title-wrapper">
    <h3 class="title">{{offer.title}}</h3>
  </div>
  <div class="picture">
    <img src="/assets/{{offer.pictureUrl}}" alt="offer-image" />
  </div>
  <div class="body">
    <div class="byline">
      <span class="user">{{offer.userName}}</span> - 
      <span class="published-on">{{offer.publishedOn | date: 'dd/MM/YYYY h:mm:ss a'  }}</span>
    </div>
    <p class="description">{{offer.description}}</p>
    <div class="category">{{offer.categoryName}}</div>
  </div>
</div>
