import { Component, Input, OnInit } from '@angular/core';
import { PageModel } from '../../models/page.model';
import { OfferModel } from '../../marketplace-api/models/offer.model';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-paginator',
  templateUrl: './paginator.component.html',
  styleUrls: ['./paginator.component.scss'],
  standalone: true,
  imports: [
    CommonModule
  ]
})
export class PaginatorComponent implements OnInit {
  Mat = Math

  pagesArr: Array<number>; //Array used to generate page buttons

  offset: number = 0;

  _pageConfig: PageModel<OfferModel>;

  @Input() set pageConfig(pageModel: PageModel<OfferModel>) {
    this._pageConfig = pageModel;
    // this._pageConfig.pageIndex=10;
    this.pagesArr = [...Array(pageModel.pageCount).keys()];
  }

  /**
   * @description Odd number that defines the max amount of page buttons to display
   */
  maxButtonsDisplayed: number = 9 


  constructor() { }

  ngOnInit(): void {
  }

  switchPage(pageIndex: number, index?){
    if(!(pageIndex < 0) && !(pageIndex > this._pageConfig.pageCount)) {
      this._pageConfig.pageIndex = pageIndex;
      // window.scroll(0,0);
      if(index >= 5){
        this.offset = (index+5) >= 10 ? (pageIndex-index) : this.offset;
      }else {
        // this.offset = 0;
      }
    } 
  }

  /**
   * @description This function calculates offset for pagination middle cursor (After reaching half of the 
   *  pagination it shouldn't reach the end of it unless on the end of the pages). Offset cursor loads new pages.
   */
  calculateOffset(){
    if(this._pageConfig.pageIndex <= 4) return 0; //Must not apply any offset if page number is less than 4.
    else if(this._pageConfig.pageCount - this._pageConfig.pageIndex <= 4){
      return this._pageConfig.pageCount - 9
    } else {
      return this._pageConfig.pageIndex - 4
    }
    //return this._pageConfig.pageIndex <= 4 ? 0: this._pageConfig.pageIndex - (this._pageConfig.pageCount - this._pageConfig.pageIndex <= 4 ? 0 : 4);
  }
}
