import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { BehaviorSubject, Subject, of } from 'rxjs';
import { environment } from 'src/environments/environment';

@Injectable({
  providedIn: 'root'
})
export class AuthService {

  private _userSessionListener = async (storage) => {
    if(storage["username"]){
      const existingUser = await this.getUser(storage["username"]).toPromise();
      console.log(existingUser);
    }
  }

  private _user = new BehaviorSubject<string | null>(null);
  public $user = this._user.asObservable();

  constructor(private http: HttpClient) { 

    if (window.addEventListener) {
      window.addEventListener("storage", this._userSessionListener, false);
    }

  }

  getUser(username){
    return this.http.get(`${environment.apiUrl}/User/GetUserByUserName`, { params: { username } })
  }

  logIn(username){
    localStorage.setItem("username", username)
  }

  signOut(){
    localStorage.removeItem("username");
  }
}
