import { Component, OnInit } from '@angular/core';
import { OfferItemComponent } from '../offer-item/offer-item.component';
import { OfferModel } from 'src/app/core/marketplace-api/models/offer.model';
import { CommonModule } from '@angular/common';
import { PaginatorComponent } from 'src/app/core/components/paginator/paginator.component';
import { PageModel } from 'src/app/core/models/page.model';

@Component({
  selector: 'app-offer-list',
  templateUrl: './offer-list.component.html',
  styleUrls: ['./offer-list.component.scss'],
  standalone: true,
  imports: [
    CommonModule, 
    OfferItemComponent,
    PaginatorComponent
  ]
})
export class OfferListComponent implements OnInit {
  offerList: OfferModel[] = []
  pageConfig: PageModel<OfferModel>;


  constructor() {
    const data = [
      {
        title: "IPhone",
        category: "Used",
        description: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Morbi a augue tristique, aliquam purus in, varius diam. Sed sagittis interdum orci, sed fringilla diam mattis sed.",
        location: "Bogotá D.C",
        pictureUrl: "iphone.jpg",
        publishedOn: new Date(),
        user: "Fabián"
      },
      {
        title: "Sofa",
        category: "New",
        description: "Aliquam purus in, varius diam. Sed sagittis interdum orci, sed fringilla diam mattis sed.",
        location: "Cali",
        pictureUrl: "sofa.jpg",
        publishedOn: new Date(),
        user: "Alberto"
      },
      {
        title: "Puff",
        category: "Used",
        description: "Sed sagittis interdum orci, sed fringilla diam mattis sed.",
        location: "Bogotá D.C",
        pictureUrl: "puff.jpg",
        publishedOn: new Date(),
        user: "David"
      },
      {
        title: "Lantern",
        category: "New",
        description: "Consectetur adipiscing elit. Interdum orci, sed fringilla diam mattis sed.",
        location: "Medellín",
        pictureUrl: "lantern.jpg",
        publishedOn: new Date(),
        user: "Juan"
      },
      {
        title: "Summer House",
        category: "For Rent",
        description: "Morbi a augue tristique, aliquam purus in, varius diam.  a augue tristique, aliquam purus in, varius",
        location: "Bogotá D.C",
        pictureUrl: "summer-house.jpg",
        publishedOn: new Date(),
        user: "David"
      }
    ];

    this.offerList = Array(20).fill([...data]).reduce((a, b) => a.concat(b));
    this.pageConfig = new PageModel<OfferModel>(this.offerList, 0, Math.ceil(this.offerList.length / 6))
  }

  ngOnInit(): void {
  }

}
