import {Component, Input, OnInit} from '@angular/core';
import {FormControl, FormGroup, ReactiveFormsModule, UntypedFormGroup, Validators} from '@angular/forms';
import { Category } from 'src/app/core/models/category.model';
import { OfferService } from 'src/app/core/services/offer.service';

@Component({
  selector: 'app-offer-creation',
  templateUrl: './offer-creation.component.html',
  styleUrls: ['./offer-creation.component.scss'],
  standalone: true,
  imports: [ReactiveFormsModule],
})
export class OfferCreationComponent implements OnInit {

  offerForm = new FormGroup({
    categoryId: new FormControl('', [Validators.required]),
    description: new FormControl('', [Validators.required]),
    location: new FormControl('', [Validators.required]),
    pictureUrl: new FormControl('', [Validators.required]),
    title: new FormControl('', [Validators.required]),
  });

  categories: Category[];

  constructor(private offerService: OfferService) { }

  ngOnInit(): void {
  }

  async offerSubmit(){
    // if(this.offerForm.valid){
    //   try{
    //     const id = await this.auth.signUp(this.signUpForm.controls.username.value).toPromise()
    //     if(id){
    //       alert("User created successfully")
    //       this.router.navigate(["/login"]);
    //     } else {
    //       alert("An error occured while creating the user")
    //     }
    //   } catch(e){
    //     console.log(e);
    //   }
    // } else {
    //   alert("You must enter a valid post offer")
    // }
  }

  async populateCategories(){
    this.categories = await this.offerService.getCategories();
  }
}
<form class="offer-form">
  <h3>Post new Offer</h3>
  <div class="form-group">
    <label for="title">Title</label>
    <input id="title" class="" placeholder="Title"/>
  </div>
  <div class="form-group">
    <label for="picture-url">Picture URL</label>
    <input id="picture-url" placeholder="iphone.jpg"/>
  </div>
  <div class="form-group">
    <label for="description">Description</label>
    <textarea id="description" class="" placeholder="Description" rows="5"></textarea>
  </div>
  <div class="form-group">
    <label for="location">Location</label>
    <input id="location" class="" placeholder="Country, city, neighborhood, the office, my desk, etc..."/>
  </div>
  <div class="form-group">
    <label for="category">Category</label>
    <select id="category" class="">
      <option value="1">test</option>
      <option [value]="item" *ngFor="let item of categories">{{item}}</option>
    </select>
  </div>
  <button type="submit" class="btn primary-btn offer-submit-button">Create</button>
</form>
